import React from "react";

const RadioOption = ({ title, imgSrc }) => {
  return (
    <div>
      <input type="radio" name="radio" id={option} />
      <label htmlFor={option}>
        {title && <h1>{title}</h1>}
        {imgSrc && <img src={`${imgSrc}`} alt="" />}
      </label>
    </div>
  );
};

export default RadioOption;
