import React from "react";

const RadioOption = ({ title, imgSrc, option }) => {
  const handleChange = () => {
    alert("Changed");
  };

  return (
    <div className="RadioOption">
      <input
        type="radio"
        name="radio"
        id={option}
        value={option}
        onChange={() => handleChange()}
      />
      <label htmlFor={option}>
        {imgSrc && <img src={`${imgSrc}`} alt="" />}
        {title && <h1>{title}</h1>}
      </label>
    </div>
  );
};

export default RadioOption;
